import React, { useState } from 'react';
import axios from 'axios'; // You may need to install axios if you haven't already
import './src/index.css';

const CreateEmployee = () => {
  const [employee, setEmployee] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: [],
    image: null
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setEmployee((prevState) => ({
        ...prevState,
        course: checked
          ? [...prevState.course, value]
          : prevState.course.filter((item) => item !== value)
      }));
    } else if (type === 'file') {
      setEmployee((prevState) => ({
        ...prevState,
        [name]: e.target.files[0]
      }));
    } else {
      setEmployee((prevState) => ({
        ...prevState,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    for (const key in employee) {
      if (Array.isArray(employee[key])) {
        employee[key].forEach((item) => formData.append(key, item));
      } else {
        formData.append(key, employee[key]);
      }
    }

    try {
      await axios.post('/api/employees', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Employee created successfully!');
      // Optionally redirect or clear the form
    } catch (error) {
      console.error('Error creating employee:', error);
      alert('Failed to create employee.');
    }
  };

  return (
    <div>
      <h2>Create Employee</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={employee.name} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={employee.email} onChange={handleChange} required />
        </label>
        <label>
          Mobile No:
          <input type="tel" name="mobile" value={employee.mobile} onChange={handleChange} required />
        </label>
        <label>
          Designation:
          <select name="designation" value={employee.designation} onChange={handleChange} required>
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </label>
        <label>
          Gender:
          <label>
            <input type="radio" name="gender" value="Male" checked={employee.gender === 'Male'} onChange={handleChange} />
            Male
          </label>
          <label>
            <input type="radio" name="gender" value="Female" checked={employee.gender === 'Female'} onChange={handleChange} />
            Female
          </label>
        </label>
        <label>
          Course:
          <label>
            <input type="checkbox" name="course" value="MCA" checked={employee.course.includes('MCA')} onChange={handleChange} />
            MCA
          </label>
          <label>
            <input type="checkbox" name="course" value="BCA" checked={employee.course.includes('BCA')} onChange={handleChange} />
            BCA
          </label>
          <label>
            <input type="checkbox" name="course" value="BSC" checked={employee.course.includes('BSC')} onChange={handleChange} />
            BSC
          </label>
        </label>
        <label>
          Img Upload:
          <input type="file" name="image" onChange={handleChange} />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreateEmployee;
