import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeList.css'; // Ensure this CSS file exists

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/employees');
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleEdit = async (index) => {
    const updatedEmployee = { /* Collect updated data here */ };
    try {
      await axios.put(`http://localhost:5000/api/employees/${index}`, updatedEmployee);
      fetchEmployees(); // Refresh the employee list
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async (index) => {
    try {
      await axios.delete(`http://localhost:5000/api/employees/${index}`);
      fetchEmployees(); // Refresh the employee list
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="employee-list-container">
      <h2>Employee List</h2>
      <table>
        <thead>
          <tr>
            <th>Unique Id</th>
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile No</th>
            <th>Designation</th>
            <th>Gender</th>
            <th>Course</th>
            <th>Create Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td><img src={`http://localhost:5000/${emp.image}`} alt="Employee" className="employee-image" /></td>
              <td>{emp.name}</td>
              <td>{emp.email}</td>
              <td>{emp.mobile}</td>
              <td>{emp.designation}</td>
              <td>{emp.gender}</td>
              <td>{Array.isArray(emp.course) ? emp.course.join(', ') : emp.course}</td>
              <td>{new Date(emp.createdAt).toLocaleDateString()}</td>
              <td>
                <button onClick={() => handleEdit(index)}>Edit</button>
                <button onClick={() => handleDelete(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
